-- Schema and seed data for MariaDB
-- Generated from existing in-app data structures
SET NAMES utf8mb4;
SET time_zone = "+00:00";

CREATE TABLE IF NOT EXISTS zones (
    zone_id VARCHAR(64) PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description_short TEXT,
    description_long TEXT,
    zone_type VARCHAR(50),
    recommended_level_min INT,
    recommended_level_max INT,
    is_safe_zone TINYINT(1) DEFAULT 0,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP
);

CREATE TABLE IF NOT EXISTS rooms (
    room_id INT AUTO_INCREMENT PRIMARY KEY,
    zone_id VARCHAR(64) NOT NULL,
    name VARCHAR(255) NOT NULL,
    description_base TEXT,
    description_searched TEXT,
    x_coord INT NOT NULL,
    y_coord INT NOT NULL,
    is_starting_room TINYINT(1) DEFAULT 0,
    is_town_portal_target TINYINT(1) DEFAULT 0,
    is_safe_room TINYINT(1) DEFAULT 0,
    light_level VARCHAR(50),
    search_dc INT,
    notes_gm JSON,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    CONSTRAINT fk_rooms_zone FOREIGN KEY (zone_id) REFERENCES zones(zone_id)
);

CREATE TABLE IF NOT EXISTS room_exits (
    exit_id INT AUTO_INCREMENT PRIMARY KEY,
    from_room_id INT NOT NULL,
    to_room_id INT NOT NULL,
    direction VARCHAR(16) NOT NULL,
    is_locked TINYINT(1) DEFAULT 0,
    lock_dc INT,
    requires_key_item_id VARCHAR(64),
    is_hidden TINYINT(1) DEFAULT 0,
    discover_dc INT,
    notes_gm TEXT,
    CONSTRAINT fk_exits_from_room FOREIGN KEY (from_room_id) REFERENCES rooms(room_id),
    CONSTRAINT fk_exits_to_room FOREIGN KEY (to_room_id) REFERENCES rooms(room_id)
);

CREATE TABLE IF NOT EXISTS mob_templates (
    mob_template_id VARCHAR(64) PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    size VARCHAR(32),
    creature_type VARCHAR(64),
    tags JSON,
    alignment VARCHAR(32),
    cr DECIMAL(4,2),
    xp_value INT,
    proficiency_bonus INT,
    armor_class INT,
    ac_notes TEXT,
    hp_average INT,
    hp_dice VARCHAR(32),
    speed_walk INT,
    speed_swim INT,
    speed_climb INT,
    speed_fly INT,
    initiative_mod INT,
    initiative_fixed INT,
    str_score INT,
    dex_score INT,
    con_score INT,
    int_score INT,
    wis_score INT,
    cha_score INT,
    str_save_mod INT,
    dex_save_mod INT,
    con_save_mod INT,
    int_save_mod INT,
    wis_save_mod INT,
    cha_save_mod INT,
    skill_proficiencies TEXT,
    resistances TEXT,
    vulnerabilities TEXT,
    immunities TEXT,
    senses TEXT,
    languages TEXT,
    traits_json JSON,
    actions_json JSON,
    bonus_actions_json JSON,
    reactions_json JSON,
    legendary_actions_json JSON,
    aggro_type VARCHAR(32),
    default_disposition VARCHAR(32),
    patrol_pattern TEXT,
    attack_interval DECIMAL(6,2),
    notes_gm JSON
);

CREATE TABLE IF NOT EXISTS room_mob_spawns (
    room_mob_spawn_id INT AUTO_INCREMENT PRIMARY KEY,
    room_id INT NOT NULL,
    mob_template_id VARCHAR(64) NOT NULL,
    min_count INT DEFAULT 1,
    max_count INT DEFAULT 1,
    respawn_seconds INT DEFAULT 0,
    initial_spawn_count INT DEFAULT 1,
    notes_gm TEXT,
    CONSTRAINT fk_spawn_room FOREIGN KEY (room_id) REFERENCES rooms(room_id),
    CONSTRAINT fk_spawn_mob FOREIGN KEY (mob_template_id) REFERENCES mob_templates(mob_template_id)
);

CREATE TABLE IF NOT EXISTS mob_instances (
    mob_instance_id INT AUTO_INCREMENT PRIMARY KEY,
    mob_template_id VARCHAR(64) NOT NULL,
    room_id INT,
    current_hp INT,
    temporary_hp INT,
    conditions JSON,
    cooldown_until DATETIME,
    status VARCHAR(32),
    last_aggro_player_id INT,
    custom_name VARCHAR(255),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    last_updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    CONSTRAINT fk_instance_template FOREIGN KEY (mob_template_id) REFERENCES mob_templates(mob_template_id),
    CONSTRAINT fk_instance_room FOREIGN KEY (room_id) REFERENCES rooms(room_id)
);

CREATE TABLE IF NOT EXISTS npc_templates (
    npc_template_id VARCHAR(64) PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    size VARCHAR(32),
    creature_type VARCHAR(64),
    alignment VARCHAR(32),
    role VARCHAR(64),
    dialogue_profile_id VARCHAR(64),
    armor_class INT,
    hp_average INT,
    hp_dice VARCHAR(32),
    speed_walk INT,
    str_score INT,
    dex_score INT,
    con_score INT,
    int_score INT,
    wis_score INT,
    cha_score INT,
    str_save_mod INT,
    dex_save_mod INT,
    con_save_mod INT,
    int_save_mod INT,
    wis_save_mod INT,
    cha_save_mod INT,
    senses TEXT,
    languages TEXT,
    traits_json JSON,
    is_attackable TINYINT(1) DEFAULT 0
);

CREATE TABLE IF NOT EXISTS room_npc_spawns (
    room_npc_spawn_id INT AUTO_INCREMENT PRIMARY KEY,
    room_id INT NOT NULL,
    npc_template_id VARCHAR(64) NOT NULL,
    is_unique TINYINT(1) DEFAULT 1,
    auto_respawn TINYINT(1) DEFAULT 0,
    respawn_seconds INT DEFAULT 0,
    notes_gm TEXT,
    CONSTRAINT fk_room_npc_room FOREIGN KEY (room_id) REFERENCES rooms(room_id),
    CONSTRAINT fk_room_npc_template FOREIGN KEY (npc_template_id) REFERENCES npc_templates(npc_template_id)
);

CREATE TABLE IF NOT EXISTS item_templates (
    item_template_id VARCHAR(64) PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    item_type VARCHAR(64),
    rarity VARCHAR(32),
    weight DECIMAL(10,2),
    value_gp DECIMAL(10,2),
    description TEXT,
    weapon_category VARCHAR(64),
    damage_dice VARCHAR(32),
    damage_type VARCHAR(32),
    range_normal INT,
    range_long INT,
    armor_type VARCHAR(64),
    armor_class_base INT,
    armor_requires_training TINYINT(1),
    consumable_effect_json JSON,
    equippable_slot VARCHAR(64)
);

CREATE TABLE IF NOT EXISTS item_instances (
    item_instance_id INT AUTO_INCREMENT PRIMARY KEY,
    item_template_id VARCHAR(64) NOT NULL,
    owner_character_id INT,
    owner_mob_instance_id INT,
    room_id INT,
    stack_size INT DEFAULT 1,
    is_equipped TINYINT(1) DEFAULT 0,
    equipped_slot VARCHAR(64),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    CONSTRAINT fk_instance_template_item FOREIGN KEY (item_template_id) REFERENCES item_templates(item_template_id),
    CONSTRAINT fk_instance_room_item FOREIGN KEY (room_id) REFERENCES rooms(room_id)
);

CREATE TABLE IF NOT EXISTS room_loot_tables (
    room_loot_id INT AUTO_INCREMENT PRIMARY KEY,
    room_id INT NOT NULL,
    item_template_id VARCHAR(64) NOT NULL,
    drop_chance DECIMAL(5,2) DEFAULT 1.00,
    max_quantity INT DEFAULT 1,
    requires_successful_search TINYINT(1) DEFAULT 0,
    min_search_dc INT,
    CONSTRAINT fk_room_loot_room FOREIGN KEY (room_id) REFERENCES rooms(room_id),
    CONSTRAINT fk_room_loot_item FOREIGN KEY (item_template_id) REFERENCES item_templates(item_template_id)
);

CREATE TABLE IF NOT EXISTS accounts (
    account_id INT AUTO_INCREMENT PRIMARY KEY,
    username VARCHAR(150) UNIQUE NOT NULL,
    password_hash VARCHAR(255) NOT NULL,
    email VARCHAR(255),
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    last_login_at TIMESTAMP
);

CREATE TABLE IF NOT EXISTS characters (
    character_id INT AUTO_INCREMENT PRIMARY KEY,
    account_id INT NOT NULL,
    name VARCHAR(150) UNIQUE NOT NULL,
    species VARCHAR(64),
    background VARCHAR(128),
    class VARCHAR(64),
    level INT DEFAULT 1,
    xp INT DEFAULT 0,
    current_room_id INT,
    last_zone_id VARCHAR(64),
    last_saved_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    str_score INT,
    dex_score INT,
    con_score INT,
    int_score INT,
    wis_score INT,
    cha_score INT,
    proficiency_bonus INT,
    max_hp INT,
    current_hp INT,
    temporary_hp INT,
    hit_dice VARCHAR(64),
    armor_class INT,
    initiative_mod INT,
    speed_walk INT,
    passive_perception INT,
    str_save_mod INT,
    dex_save_mod INT,
    con_save_mod INT,
    int_save_mod INT,
    wis_save_mod INT,
    cha_save_mod INT,
    skill_mods_json JSON,
    languages TEXT,
    conditions JSON,
    coin_gp INT DEFAULT 0,
    death_saves_success INT DEFAULT 0,
    death_saves_fail INT DEFAULT 0,
    weapon_inventory JSON,
    item_inventory JSON,
    equipped_weapon VARCHAR(64),
    bio TEXT,
    description TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
    FOREIGN KEY (account_id) REFERENCES accounts(account_id),
    FOREIGN KEY (current_room_id) REFERENCES rooms(room_id),
    FOREIGN KEY (last_zone_id) REFERENCES zones(zone_id)
);

CREATE TABLE IF NOT EXISTS active_effects (
    effect_id INT AUTO_INCREMENT PRIMARY KEY,
    target_type VARCHAR(32) NOT NULL,
    target_id INT NOT NULL,
    effect_name VARCHAR(128) NOT NULL,
    effect_type VARCHAR(64),
    source VARCHAR(128),
    expires_at TIMESTAMP,
    effect_data_json JSON
);

-- Zones seed data
INSERT INTO zones (zone_id, name, description_short, description_long, zone_type, recommended_level_min, recommended_level_max, is_safe_zone) VALUES ('village', 'Greyford Village', 'A frontier village with warpstone access to nearby dungeons.', 'Greyford Village bustles with traders and adventurers preparing to explore the surrounding wilds.', 'settlement', 1, 3, 1) ON DUPLICATE KEY UPDATE name=VALUES(name), description_short=VALUES(description_short), description_long=VALUES(description_long), zone_type=VALUES(zone_type), recommended_level_min=VALUES(recommended_level_min), recommended_level_max=VALUES(recommended_level_max), is_safe_zone=VALUES(is_safe_zone);
INSERT INTO zones (zone_id, name, description_short, description_long, zone_type, recommended_level_min, recommended_level_max, is_safe_zone) VALUES ('dungeon_1', 'Old Mine', 'An abandoned mine infested with vermin and goblins.', 'Collapsed tunnels and hidden warpstones criss-cross the old mine''s upper levels.', 'dungeon', 1, 2, 0) ON DUPLICATE KEY UPDATE name=VALUES(name), description_short=VALUES(description_short), description_long=VALUES(description_long), zone_type=VALUES(zone_type), recommended_level_min=VALUES(recommended_level_min), recommended_level_max=VALUES(recommended_level_max), is_safe_zone=VALUES(is_safe_zone);
INSERT INTO zones (zone_id, name, description_short, description_long, zone_type, recommended_level_min, recommended_level_max, is_safe_zone) VALUES ('dungeon_2', 'Crystal Depths', 'Crystal caverns humming with elemental resonance.', 'Prismatic crystals and kobold guardians protect the depths of the crystal caverns.', 'dungeon', 2, 4, 0) ON DUPLICATE KEY UPDATE name=VALUES(name), description_short=VALUES(description_short), description_long=VALUES(description_long), zone_type=VALUES(zone_type), recommended_level_min=VALUES(recommended_level_min), recommended_level_max=VALUES(recommended_level_max), is_safe_zone=VALUES(is_safe_zone);

-- Rooms seed data
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (1, 'village', 'Northern Gate', 'Timber palisades creak while a sentry nods beneath the gate arch. Beyond the arch the trade road stretches north into the frontier, while the village lane bends east toward Merchant Row and south toward the Thatched Cottages; the palisade crowds close along the western wall.', NULL, 0, 0, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (2, 'village', 'Merchant Row', 'Stalls line the street with bolts of cloth and fresh produce neatly displayed. A narrow cart track returns west to the Northern Gate, the shrine''s stone steps rise to the east, and a footpath drops south into the Herbalist Garden; the palisade seals away any path to the north.', NULL, 1, 0, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (3, 'village', 'Shrine Steps', 'Stone steps lead to a modest shrine where votive candles gutter in the breeze. The plaza spills west onto Merchant Row, a shadowed watchtower yard waits to the east, and the main thoroughfare continues south toward the Town Hall; the cliff of the palisade blocks any climb northward.', NULL, 2, 0, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (4, 'village', 'Watchtower Shadow', 'The shadow of the wooden watchtower stretches across crates of supplies. Patrol stairs descend west toward the shrine, a service lane leads east toward the crumbling wall, and a ramp descends south into the Storage Barn''s yard; the timber barricade hugs the northern edge.', NULL, 3, 0, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (5, 'village', 'Crumbling Wall', 'Weathered masonry from an older fortification lies half-buried in the sod. The watchtower yard lies to the west and the wagon yard spreads to the south, while shattered stone and the outer ditch block passage further north or east.', NULL, 4, 0, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (6, 'village', 'Thatched Cottages', 'Smoke from cookfires drifts lazily above tidy thatched cottages. A muddy lane heads north to the Northern Gate, the herb garden blooms eastward, and baker''s apprentices dash south down Baker''s Lane; pastures hem in the cottages to the west.', NULL, 0, 1, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (7, 'village', 'Herbalist Garden', 'Raised beds of fragrant herbs attract bees and the occasional wandering villager. Wicker gates swing west toward the thatched cottages, the Town Hall stands east beyond tidy hedges, merchant stalls bustle to the south, and the market road climbs north toward Merchant Row.', NULL, 1, 1, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (8, 'village', 'Town Hall', 'The timbered town hall smells of parchment and warm lamp oil. Stone steps climb north toward the shrine, civic notices point south toward the Village Square, and the public lane angles west into the Herbalist Garden. A thick oak service door with iron bands stands in the east wall, linking the hall to the Storage Barn beyond.', NULL, 2, 1, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (9, 'village', 'Storage Barn', 'Barrels of grain and bundled hay crowd the floor and loft. The watchtower yard opens northward, the wagon yard sprawls to the east, and a work path slopes south toward the Blacksmith Forge. A sturdy service door in the west wall leads back into the Town Hall, its hinges groaning when opened.', NULL, 3, 1, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (10, 'village', 'Wagon Yard', 'Wooden wagons await repair beside piles of seasoned lumber. The crumbling wall looms to the north, the mine entrance waits to the south, and teamsters can retreat west into the Storage Barn while the outer bank blocks travel further east.', NULL, 4, 1, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (11, 'village', 'Baker''s Lane', 'Warm smells of bread drift from an open oven while apprentices hurry by. The cottage lane climbs north toward the homesteads, market stalls bustle to the east, and the forest path winds south beneath the trees; stacked wood piles close off the western fence.', NULL, 0, 2, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (12, 'village', 'Market Stalls', 'Vendors haggle over coppers as curious travelers browse their wares. Merchant Row beckons north, the Village Square thrums to the east, the Ancient Well lies down a shaded path to the south, and Baker''s Lane curls back to the west.', NULL, 1, 2, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (13, 'village', 'Village Square', 'A cobbled square with a communal well and a notice board layered with fresh ink. The Town Hall towers to the north, the forge blazes to the east, Lakeside Dock extends south, and market stalls throng to the west.', NULL, 2, 2, 1, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (14, 'village', 'Blacksmith Forge', 'Sparks fly from the anvil as the smith hammers glowing iron. The Storage Barn offers supplies to the north, the Old Mine Entrance yawns to the east, Fisher''s Hut nestles south along the water, and the Village Square rests to the west.', NULL, 3, 2, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (15, 'village', 'Old Mine Entrance', 'Timber supports frame the old mine entrance where a rune-cut warp stone hums beside the cart tracks. Wagon ruts return north to the yard, the lakefront trail bends south toward an abandoned shed, and the forge''s sparks dance to the west while sheer rock prevents travel further east.', NULL, 4, 2, 0, 1, 'bright', NULL, '{"warp": {"description": "A waist-high warp stone thrums with stored power, waiting for a willing traveler.", "destination": {"zone": "dungeon_1", "start": [3, 0]}}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (16, 'village', 'Forest Path', 'A narrow track winds beneath dark pines east of the village. Baker''s Lane lies to the north, the Ancient Well waits to the east, the South Field stretches southward, and the treeline thickens impassably to the west.', NULL, 0, 3, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (17, 'village', 'Ancient Well', 'An ivy-wrapped stone well whispers with echoes from below. Market Stalls bustle to the north, Lakeside Dock creaks to the east, the Hayloft ladder rises to the south, and the forest path circles back west.', 'You notice a loose capstone and uncover a forgotten brooch tucked inside.', 1, 3, 0, 1, 'bright', 11, '{"search": {"ability": "wis", "failure_text": "Cold water splashes your hands but nothing else reveals itself.", "loot": ["forgotten_brooch"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (18, 'village', 'Lakeside Dock', 'Wooden planks creak as small fishing boats bob against the pilings. The Village Square bustles to the north, Fisher''s Hut leans east along the shore, the Sunken Stair descends southward, and the well path loops back west.', NULL, 2, 3, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (19, 'village', 'Fisher''s Hut', 'Nets dry on racks beside a cottage that smells strongly of smoked trout. The forge blazes to the north, the abandoned shed lists to the east, Miller''s Bridge crosses the millrace to the south, and Lakeside Dock lies to the west.', NULL, 3, 3, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (20, 'village', 'Abandoned Shed', 'Broken tools litter a leaning shed reclaimed by moss. The mine tracks return north, Riverside Grove murmurs to the south, and Fisher''s Hut stands to the west while thick bramble chokes the eastern approach.', NULL, 4, 3, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (21, 'village', 'South Field', 'Rows of freshly turned soil promise a hearty autumn harvest. The forest path returns north, the Hayloft beckons east beside the barn, and hedgerows fence off the farmland to the south and west.', NULL, 0, 4, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (22, 'village', 'Hayloft', 'Stacks of hay tower above the barn floor, a favorite hideout for village children. The Ancient Well rests to the north, the Sunken Stair descends east toward the cellar, and the South Field lies to the west while corrals close the way south.', NULL, 1, 4, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (23, 'village', 'Sunken Stair', 'Stone steps descend into a cellar where a crystal-veined warp stone casts steady, cold light. Lakeside Dock rests to the north, Miller''s Bridge arches east, the Hayloft is tucked to the west, and packed earth walls bar any southern exit.', NULL, 2, 4, 0, 1, 'bright', NULL, '{"warp": {"description": "An embedded warp stone glows between the lowest steps, promising passage for those who touch it.", "destination": {"zone": "dungeon_2", "start": [4, 0]}}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (24, 'village', 'Miller''s Bridge', 'A wooden bridge spans the millrace, slick with fine spray. Fisher''s Hut perches to the north, Riverside Grove rustles to the east, and the Sunken Stair is only a few steps west while foaming water bars travel south.', NULL, 3, 4, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (25, 'village', 'Riverside Grove', 'Tall willows shade a quiet bend in the river where frogs croak at dusk. The abandoned shed leans to the north, Miller''s Bridge sits to the west, and the deep river curls around to block any southern or eastern approach.', NULL, 4, 4, 0, 1, 'bright', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (26, 'dungeon_1', 'Sealed Tunnel', 'Rockfalls here leave only a narrow crawlspace. A jagged crawl squeezes east toward the Dusty Landing and a cracked slope drops south into the Twisting Hall, while collapsed stone seals the way north and west.', NULL, 0, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (27, 'dungeon_1', 'Dusty Landing', 'Loose gravel litters the floor where miners once gathered. The crawlspace leads back west to the Sealed Tunnel, stalagmites knife up to the east, and a greasy ramp slides south toward the Rat Warren; the ceiling presses too low to continue north.', NULL, 1, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (28, 'dungeon_1', 'Stalagmite Cluster', 'Jagged pillars create cramped lanes through the cavern. Lantern light glimmers on the Collapsed Entrance to the east, the Dusty Landing lies to the west, and a damp pit yawns south toward the Sunken Den; fallen spires choke off the northern crawl.', 'You pry apart two stalagmites and find a miner''s scribbled map fragment.', 2, 0, 0, 0, 'dim', 12, '{"search": {"ability": "int", "failure_text": "Every crevice looks the same in the dim torchlight.", "loot": ["ancient_coin"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (29, 'dungeon_1', 'Collapsed Entrance', 'Splintered beams circle a collapsed shaft where a cracked warp stone glows amid the rubble. The rubble slope leads west toward the stalagmite cluster, a narrow chute dives east, and the Broken Cart chamber lies directly south. The warp stone hums with power for those returning to the village above.', NULL, 3, 0, 1, 0, 'dim', NULL, '{"warp": {"description": "A chipped warp stone flickers here, still strong enough to return travelers to the village.", "destination": {"zone": "village", "start": [2, 2]}}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (30, 'dungeon_1', 'Rubble Chute', 'Fresh stones tumble occasionally from the sloped ceiling. The Collapsed Entrance rests to the west, the Echoing Gallery opens eastward, and pungent fungal growth spreads south into the grotto while unstable debris blocks any path north.', NULL, 4, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (31, 'dungeon_1', 'Echoing Gallery', 'Footsteps clap back in a chorus of hollow echoes. The rubble chute narrows to the west, the Dripping Alcove glistens to the east, and a cold underground brook courses south; the ceiling squeezes low to the north.', NULL, 5, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (32, 'dungeon_1', 'Dripping Alcove', 'Mineral-heavy droplets patter into a shallow basin. Echoing passages return west, slick stairs spiral south toward the lower levels, and the alcove dead-ends against sheer rock to the east and north.', NULL, 6, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (33, 'dungeon_1', 'Twisting Hall', 'Passages coil like a knot, worn smooth by decades of traffic. The Sealed Tunnel lies to the north, vermin tunnels wriggle east toward the Rat Warren, and a rubble slide drops south into a collapsed shaft while the western wall remains sealed.', NULL, 0, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (34, 'dungeon_1', 'Rat Warren', 'Nests of frayed rope and cloth rustle with vermin. The Dusty Landing opens to the north, the Sunken Den reeks to the east, and a whispering junction lies south; gnawed stone limits movement back west to the twisting hall.', NULL, 1, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (35, 'dungeon_1', 'Sunken Den', 'Moisture collects in a low pit surrounded by gnawed bones. The stalagmite cluster looms to the north, a broken ore cart lies to the east, goblin sentries muster to the south, and the rat warrens open to the west.', NULL, 2, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (36, 'dungeon_1', 'Broken Cart', 'A splintered ore cart lies on its side, spilling rusted tools. The collapsed entrance glows to the north, fungal terraces shimmer to the east, goblin fields extend south, and the sunken den rests to the west.', NULL, 3, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (37, 'dungeon_1', 'Fungus Grotto', 'Bioluminescent caps cast a ghostly glow over the cavern. The rubble chute feeds spores from the north, an underground brook courses east, a guard post waits to the south, and the broken cart chamber lies to the west.', NULL, 4, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (38, 'dungeon_1', 'Underground Brook', 'An icy stream carves a shallow trench through the stone. Echoes tumble from the gallery to the north, slick steps descend east, abandoned barracks huddle to the south, and the fungus grotto glows to the west.', NULL, 5, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (39, 'dungeon_1', 'Slick Steps', 'A carved stairway descends deeper, slick with condensation. The dripping alcove is just north, the underground brook winds to the west, and a shimmering pool lies directly south while crumbling rock blocks any path eastward.', NULL, 6, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (40, 'dungeon_1', 'Collapsed Shaft', 'The remnants of a vertical shaft are choked with rubble. Twisting passages climb north, a whispering fork opens east, and a bone-strewn pit falls away to the south while the western wall remains buried.', NULL, 0, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (41, 'dungeon_1', 'Whispering Fork', 'Whispers ride the draft, hinting at unseen side passages. Rat tunnels emerge from the north, a goblin outpost stands to the east, collapsed dormitories stretch south, and the shaft rubble presses close to the west.', NULL, 1, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (42, 'dungeon_1', 'Goblin Outpost', 'Makeshift barricades guard a cluster of stolen crates. The sunken den growls to the north, fungal farms expand eastward, a makeshift shrine flickers to the south, and the whispering fork provides a western retreat.', NULL, 2, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (43, 'dungeon_1', 'Fungal Farms', 'Rows of edible fungus grow in carefully scraped troughs. Goblin outposts bustle to the west, a guard post holds watch to the east, the overseer chamber looms south, and the broken cart room is only a stair north.', NULL, 3, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (44, 'dungeon_1', 'Guard Post', 'A pair of overturned barrels serve as a crude lookout. Spore fields lie to the west, abandoned barracks slump to the east, goblin farmers labor northward, and drills echo from the hall to the south.', NULL, 4, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (45, 'dungeon_1', 'Abandoned Barracks', 'Rotten bedrolls and broken spears lie scattered across the floor. The underground brook murmurs to the north, a shimmering pool gleams to the east, the crystal vein tunnels run south, and the guard post watches from the west.', 'Beneath a cot you discover a goblin bugle wrapped in cloth.', 5, 2, 0, 0, 'dim', 13, '{"search": {"ability": "wis", "failure_text": "Only moldy blankets and stale air greet your search.", "loot": ["goblin_bugle"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (46, 'dungeon_1', 'Shimmering Pool', 'Still water reflects the cavern roof like polished glass. Slick steps climb north, a vent shaft plunges south, and abandoned barracks lie to the west while crystal-studded walls block the eastern edge.', NULL, 6, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (47, 'dungeon_1', 'Bone Pile', 'Heap of cracked bones crunch underfoot. Rubble passages climb north to the collapsed shaft, a ruined dormitory slumps east, and molten heat rises from a lava fissure to the south while the western face is sealed.', NULL, 0, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (48, 'dungeon_1', 'Collapsed Dormitory', 'Splintered bunks sag beneath a partial ceiling collapse. Whispering passages return north, a makeshift shrine glows to the east, a chasm edge yawns south, and the bone pile lies to the west.', NULL, 1, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (49, 'dungeon_1', 'Makeshift Shrine', 'Charred sigils mark a shrine to a forgotten subterranean spirit. Goblin sentries watch from the north, the overseer chamber stands eastward, a fortified guardroom waits to the south, and collapsed dormitories slump to the west.', 'Behind the altar you uncover a pouch of ancient coins.', 2, 3, 0, 0, 'dim', 14, '{"search": {"ability": "wis", "failure_text": "The shrine offers only dust and unsettling whispers.", "loot": ["ancient_coin"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (50, 'dungeon_1', 'Overseer Chamber', 'A carved desk and ledger hint at the mine''s orderly past. Fungal farms line the northern balcony, the drill hall roars to the east, supply depots extend south, and the shrine''s altar stands to the west.', NULL, 3, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (51, 'dungeon_1', 'Drill Hall', 'Rusted drills and chains litter this wide chamber. Guard posts hold the northern arch, a crystal vein sparkles to the east, a trophy chamber stands to the south, and the overseer chamber commands the west.', NULL, 4, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (52, 'dungeon_1', 'Crystal Vein', 'Chunks of quartz jut from the walls, catching stray light. Abandoned barracks loom to the north, a vent shaft gusts eastward, hidden workshops wait to the south, and the drill hall rattles to the west.', NULL, 5, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (53, 'dungeon_1', 'Vent Shaft', 'A narrow chimney breathes cool air from unseen depths. The shimmering pool lies to the north, the lower spiral plunges south, and crystal veins glitter to the west while the eastern wall remains sheer.', NULL, 6, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (54, 'dungeon_1', 'Lava Fissure', 'A faint red glow issues from a crack radiating gentle heat. The bone pile rises to the north, a chasm edge stretches east, and molten rock bars any passage further south or west.', NULL, 0, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (55, 'dungeon_1', 'Chasm Edge', 'A sheer drop-off disappears into rumbling darkness. Collapsed dormitories lie to the north, a deep guardroom holds the eastern ledge, and the lava fissure glows to the west while the abyss blocks the southern path.', NULL, 1, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (56, 'dungeon_1', 'Deep Guardroom', 'Barricades of scavenged timber block an advance deeper inside. The makeshift shrine flickers northward, the supply depot stacks to the east, and the chasm edge yawns to the west while darkness falls away to the south.', NULL, 2, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (57, 'dungeon_1', 'Supply Depot', 'Shelves of pilfered supplies are kept in meticulous order. Overseer chambers rise to the north, trophy racks gleam to the east, deep guardrooms stand to the west, and the floor drops sharply to the south.', NULL, 3, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (58, 'dungeon_1', 'Trophy Chamber', 'Tattered banners and trophies from surface raids hang proudly. Drill halls ring just north, a hidden workshop clatters to the east, and the supply depot borders to the west while the cavern wall seals the southern edge.', 'You catalogue the trophies and spot a gleaming ceremonial dagger hidden away.', 4, 4, 0, 0, 'dim', 15, '{"search": {"ability": "int", "failure_text": "Your fingers brush dusty trophies but no hidden prizes.", "loot": ["kobold_sling"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (59, 'dungeon_1', 'Hidden Workshop', 'Tools for trap making lie scattered across stone benches. Crystal veins shimmer to the north, the lower spiral descends eastward, and the trophy chamber sits to the west while the southern rock wall is unyielding.', NULL, 5, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (60, 'dungeon_1', 'Lower Spiral', 'A tight spiral stair descends into silent blackness. The vent shaft exhales cool air from the north, the hidden workshop adjoins to the west, and sheer stone seals the south and east.', NULL, 6, 4, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (61, 'dungeon_2', 'Iridescent Approach', 'Rainbow motes drift through the humid entrance hall. Glittering runoff flows to the east, the Singing Cavern hums to the south, and jagged crystal walls prevent any retreat to the north or west.', NULL, 0, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (62, 'dungeon_2', 'Glittering Runoff', 'Streams of mineral-laden water shimmer like liquid glass. The approach opens west, shattered columns topple to the east, and a stair spirals south toward Echo Falls while crystal-crusted walls block the northern face.', NULL, 1, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (63, 'dungeon_2', 'Shattered Column', 'Crystal shards jut from a toppled pillar. Glittering runoff pools to the west, the Crystal Gate rises to the east, and the Kobold Watchpost waits down a ramp to the south while the ceiling presses low overhead.', NULL, 2, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (64, 'dungeon_2', 'Crystal Gate', 'A lattice of quartz bars blocks the path and glows softly. Shattered columns lie to the west, the prismatic vestibule beams to the east, and the gate itself seals the northern archway while the Crystal Gate Lattice bars passage south toward the Shimmer Forge.', NULL, 3, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (65, 'dungeon_2', 'Prismatic Vestibule', 'Spectral light spills from a pedestal where a faceted warp stone rotates slowly in mid-air. The Crystal Gate gleams to the west, a cliffside overlook stretches east, and gemcutters labor to the south while the cavern roof seals the north.', NULL, 4, 0, 1, 0, 'dim', NULL, '{"warp": {"description": "The suspended warp stone pulses invitingly, ready to fold space back to Greyford Village.", "destination": {"zone": "village", "start": [2, 2]}}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (66, 'dungeon_2', 'Facet Overlook', 'A ledge overlooks a crystalline canyon humming with resonance. The vestibule shimmers to the west, luminous cradles glow to the east, and a maze of mirrors coils south while sheer drops guard the north.', 'You chip free a flawless crystal teardrop wedged in the rock.', 5, 0, 0, 0, 'dim', 14, '{"search": {"ability": "int", "failure_text": "Your tools slip, sending shards tinkling into the abyss.", "loot": ["crystal_teardrop"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (67, 'dungeon_2', 'Luminous Cradle', 'Nestled geodes emit a gentle violet glow. The overlook lies to the west, a fractured ramp angles eastward, and glowing barracks bustle to the south while the ceiling vaults high above.', NULL, 6, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (68, 'dungeon_2', 'Fractured Ramp', 'A sloping ramp splits, descending toward resonant caverns. Luminous nests lie to the west, the rune circle glows below to the south, and splintered stone denies travel north or east.', NULL, 7, 0, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (69, 'dungeon_2', 'Singing Cavern', 'Every step sets the crystals humming in delicate harmony. The iridescent approach opens north, Echo Falls roars to the east, and azure hollows widen to the south while the western wall remains untouched.', 'Listening carefully, you find a hidden alcove containing a crystal teardrop.', 0, 1, 0, 0, 'dim', 13, '{"search": {"ability": "wis", "failure_text": "The echoes overwhelm your senses, masking any secrets.", "loot": ["crystal_teardrop"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (70, 'dungeon_2', 'Echo Falls', 'A waterfall cascades through crystalline prisms, scattering light. Glittering runoff pours from the north, the Kobold Watchpost crouches to the east, and chittering warrens stretch south while the cascade forms a barrier to the west.', NULL, 1, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (71, 'dungeon_2', 'Kobold Watchpost', 'Kobolds have carved slits into the wall for hidden crossbows. Shattered columns rise to the north, the Shimmer Forge blazes to the east, moonstone chambers lie to the south, and Echo Falls thunders to the west.', NULL, 2, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (72, 'dungeon_2', 'Shimmer Forge', 'Forges burn with blue fire, shaping crystal arrowheads. The Kobold Watchpost guards the western arch, Gemcutter''s Bench hums to the east, icy crevasses sink to the south, and the Crystal Gate Lattice looms along the northern doorway, usually sealed against intruders.', NULL, 3, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (73, 'dungeon_2', 'Gemcutter''s Bench', 'Polishing wheels spin, leaving glittering dust across the stone. The prismatic vestibule hovers to the north, the crystal maze reflects to the east, guardian constructs muster to the south, and the forge''s sparks fly to the west.', 'You collect a pouch of finely cut shards before the dust settles.', 4, 1, 0, 0, 'dim', 15, '{"search": {"ability": "int", "failure_text": "Your hands come away empty but sparkling with grit.", "loot": ["crystal_teardrop"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (74, 'dungeon_2', 'Crystal Maze', 'Mirrored walls create bewildering reflections of yourself. The overlook beckons north, glowing barracks lie to the east, resonant vaults hum to the south, and gemcutters labor to the west.', NULL, 5, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (75, 'dungeon_2', 'Glowing Barracks', 'Sleeping pallets surround lanterns filled with glowing moss. Luminous cradles rest to the north, the rune circle shimmers to the east, crystal nurseries lie to the south, and the maze''s reflections are to the west.', NULL, 6, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (76, 'dungeon_2', 'Rune Circle', 'A circle of runes thrums with latent power. The fractured ramp descends from the north, the veiled passage winds south, and glowing barracks anchor the western edge while cracked stone blocks the eastern cliff.', NULL, 7, 1, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (77, 'dungeon_2', 'Azure Hollow', 'Blue quartz formations twist like frozen waves. Singing Cavern harmonics spill from the north, chittering warrens coil to the east, and violet depths plunge south while the western wall gleams unbroken.', NULL, 0, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (78, 'dungeon_2', 'Chittering Warrens', 'Narrow burrows ring with the chatter of unseen kobolds. Echo Falls echoes to the north, Moonstone Chamber glows to the east, Hoard Gallery stretches south, and Azure Hollow opens west.', NULL, 1, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (79, 'dungeon_2', 'Moonstone Chamber', 'Soft white light spills from polished moonstones embedded in the floor. The Kobold Watchpost stands to the north, an icy crevasse chills the east, Geode Sanctum gleams to the south, and the warrens chatter to the west.', 'You locate a concealed niche holding an untouched moonstone shard.', 2, 2, 0, 0, 'dim', 14, '{"search": {"ability": "wis", "failure_text": "Reflections play tricks on your eyes, hiding any clues.", "loot": ["crystal_teardrop"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (80, 'dungeon_2', 'Icy Crevasse', 'Cold vapors billow from a deep crack rimed with frost. The Shimmer Forge smolders to the north, guardian constructs await to the east, the Glinting Archive lies south, and moonstones glow to the west.', NULL, 3, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (81, 'dungeon_2', 'Guardian Nexus', 'Crystal sentries loom over a dais carved with warding sigils. Gemcutter''s Bench is to the north, the resonant vault hums eastward, the Crystal Throne commands the south, and icy fissures chill the west.', NULL, 4, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (82, 'dungeon_2', 'Resonant Vault', 'The air vibrates with a constant low hum that prickles your teeth. The crystal maze reflects to the north, crystal nurseries brood to the east, the ritual pool lies to the south, and guardian sentries stand to the west.', NULL, 5, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (83, 'dungeon_2', 'Crystal Nursery', 'Small geodes cradle faintly glowing eggs. Glowing barracks bustle to the north, the veiled passage drifts east, darkened faults rumble south, and the resonant vault hums to the west.', NULL, 6, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (84, 'dungeon_2', 'Veiled Passage', 'Veils of hanging crystals sway gently in the draft. The rune circle is to the north, the collapsed escape slumps south, and crystal nurseries shimmer to the west while an abyss blocks the eastern rim.', NULL, 7, 2, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (85, 'dungeon_2', 'Violet Depth', 'Deep amethyst crystals pulse with a slow, steady light. Azure hollow corridors rise to the north, hoarded spoils lie to the east, and a sheer drop bars travel further south or west.', NULL, 0, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (86, 'dungeon_2', 'Hoard Gallery', 'Neat piles of sorted gemstones testify to recent raids. Chittering warrens open to the north, the Geode Sanctum glitters to the east, and violet depths border the west while the floor falls away to the south.', NULL, 1, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (87, 'dungeon_2', 'Geode Sanctum', 'A titanic geode splits open, revealing a hollow filled with riches. Moonstone chambers gleam to the north, the Glinting Archive stores records to the east, and the Hoard Gallery rests to the west while the southern wall remains sealed.', 'You pry loose a rare heartstone from the geode''s core.', 2, 3, 0, 0, 'dim', 16, '{"search": {"ability": "int", "failure_text": "The crystalline lattice refuses to part under your efforts.", "loot": ["crystal_heartstone"]}}') ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (88, 'dungeon_2', 'Glinting Archive', 'Shelves of crystal tablets refract the light into rainbow sigils. The Icy Crevasse chills the north, the Crystal Throne commands the east, and the Geode Sanctum lines the west while silence hangs over the southern descent.', NULL, 3, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (89, 'dungeon_2', 'Crystal Throne', 'An ornate seat of quartz watches over the chamber like a judge. Guardian sentries stand to the north, the ritual pool mirrors to the east, the Glinting Archive holds records to the west, and basalt walls bar any southern route.', NULL, 4, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (90, 'dungeon_2', 'Ritual Pool', 'A still pool mirrors the ceiling perfectly despite the cavern''s breeze. The resonant vault hums to the north, the darkened fault rumbles to the east, and the Crystal Throne gleams to the west while underground currents seal the south.', NULL, 5, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (91, 'dungeon_2', 'Darkened Fault', 'Shadowed cracks hint at deeper tunnels still unexplored. Crystal nurseries line the north, the collapsed escape slumps to the east, and the ritual pool shines to the west while the ground fractures into impassable darkness southward.', NULL, 6, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);
INSERT INTO rooms (room_id, zone_id, name, description_base, description_searched, x_coord, y_coord, is_starting_room, is_safe_room, light_level, search_dc, notes_gm) VALUES (92, 'dungeon_2', 'Collapsed Escape', 'A former exit lies sealed by a recent cave-in. The veiled passage whispers to the north, the darkened fault borders west, and shattered rock walls block all hope of moving south or east.', NULL, 7, 3, 0, 0, 'dim', NULL, NULL) ON DUPLICATE KEY UPDATE name=VALUES(name), description_base=VALUES(description_base), description_searched=VALUES(description_searched), x_coord=VALUES(x_coord), y_coord=VALUES(y_coord), is_starting_room=VALUES(is_starting_room), is_safe_room=VALUES(is_safe_room), light_level=VALUES(light_level), search_dc=VALUES(search_dc), notes_gm=VALUES(notes_gm);

