-- NPC templates seed data
INSERT INTO npc_templates (npc_template_id, name, size, creature_type, alignment, role, armor_class, hp_average, hp_dice, speed_walk, str_score, dex_score, con_score, int_score, wis_score, cha_score, traits_json, is_attackable) VALUES ('elder_mara', 'Elder Mara', 'Medium', 'humanoid', 'neutral good', 'Village Elder', 13, 24, NULL, 25, 9, 11, 12, 14, 15, 16, '{"bio": "A seasoned rune-keeper who shepherds Dawnfell Village and remembers the warpstone routes of old.", "personality": "warm, patient, and quietly amused by youthful bravado", "facts": ["Dawnfell Village was rebuilt atop an abandoned teleport nexus, and the warp stones reawakened only a generation ago.", "Merchants and adventurers gather in the Village Square before venturing toward the mines or river routes.", "The town hall''s clerks can mark safe paths to the Sunken Stair if you ask respectfully.", "Warp stones require calm focus\u2014touch the rune and picture the destination to travel.", "The Old Mine Entrance houses the nearest warp stone leading to the first dungeon."], "secret_fact": "A hidden warp stone shard rests beneath the shrine steps; six sincere visits awaken it as a shortcut to the Sunken Stair.", "aliases": ["mara", "elder", "elder mara", "elder_mara"], "mob_template": "npc_elder_mara"}', 0) ON DUPLICATE KEY UPDATE name=VALUES(name), armor_class=VALUES(armor_class), hp_average=VALUES(hp_average), hp_dice=VALUES(hp_dice), traits_json=VALUES(traits_json);

